import React from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';
import './Navigation.css';
import UserProfile from '../UserProfilePage';
import LoginFormPage from '../LoginFormPage';
import { useHistory } from 'react-router-dom';



function Navigation() {
    const sessionUser = useSelector(state => state.session.user);


// const handleClick = (e) => {
//     e.preventDefault();
//     if (sessionUser) {
//             <UserProfile user={sessionUser} />
//     } else {
//             <LoginFormPage />
//     }
// }

  return (
    <>
          <NavLink exact to="/">InstaNoodles</NavLink>
        <ul>
            <li>
                <NavLink exact to="/products">Shop Now</NavLink>
            </li>
            <li>
                  <button className="fa-solid fa-user" onClick={sessionUser ? history(<UserProfile user={sessionUser} />) : history(<LoginFormPage />)}></button>
                  {/* <NavLink exact to="/account" className="fa-solid fa-user"></NavLink>   */}
            </li>
        </ul>
    </>
  );
}

export default Navigation;
