import React from 'react';
import { Link, NavLink, Redirect } from 'react-router-dom';
import { useSelector } from 'react-redux';
import './Navigation.css';



function Navigation() {
    const sessionUser = useSelector(state => state.session.user);


const handleClick = (e) => {
    e.preventDefault();
    if (sessionUser) {
            <Redirect to="/account" user={sessionUser} />
    } else {
            <Redirect to="/login" />
    }
}

  return (
    <>
          <NavLink exact to="/">InstaNoodles</NavLink>
        <ul>
            <li>
                <NavLink exact to="/products">Shop Now</NavLink>
            </li>
            <li>
                  <button className="fa-solid fa-user" onClick={handleClick}></button>
                  {/* <NavLink exact to="/account" className="fa-solid fa-user"></NavLink>   */}
            </li>
        </ul>
    </>
  );
}

export default Navigation;
