import React from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';
import './Navigation.css';
import UserProfile from '../UserProfilePage';
import LoginFormPage from '../LoginFormPage';



function Navigation() {



const handleClick = (e) => {
    e.preventDefault();
    const sessionUser = useSelector(state => state.session.user);
    if (sessionUser) {
            <UserProfile user={sessionUser} />
    } else {
            <LoginFormPage />
    }
}

  return (
    <>
          <NavLink exact to="/">InstaNoodles</NavLink>
        <ul>
            <li>
                <NavLink exact to="/products">Shop Now</NavLink>
            </li>
            <li>
                  <button className="fa-solid fa-user" onClick={handleClick={sessionUser}}></button>
                  {/* <NavLink exact to="/account" className="fa-solid fa-user"></NavLink>   */}
            </li>
        </ul>
    </>
  );
}

export default Navigation;
